import React, { Component } from 'react';
import './DisplayList.css';


export default class DisplayList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            length: 0, 
            playlistItems:[]
        };
        this.add=this.add.bind(this)
    }

    add(item){
        let total=this.state.length+item.length
        let playlistList=this.state.playlistItems
        playlistList.push(item)
        this.setState({
            length: total,
            playlistItems:playlistList

        });

    }

    render(){
        return(
            <div className="container">
                <div>
                    <p>Currently Running on Broadway: <button>All</button> <button>Yes</button><button>No</button></p>
                    <p>Won Tony Award for Best Musical: <button>All</button> <button>Yes</button><button>No</button></p>
                    <p>Sort by Length:<button>Shortest to Longest</button><button>Longest to Shortest</button></p>
                </div>
                <div className="cardContainer">
                    {this.props.list.map(item => 
                        <div className="card">
                            <img src={item.img} />
                            <div className="cardInfo">
                                <h2>{item.name}</h2>
                                <p>Won Tony Award for Best Musical: {item.tony}</p>
                                <p>Currently Running on Broadway: {item.running}</p>
                                <p>Album Length: {item.length} minutes</p>
                                <button onClick={() => this.add(item)}>Add to Playlist</button>
                            </div>
                        </div>
                    )}
                </div>
                <div className="playlist">
                    Playlist
                    {this.state.playlistItems.map(item => 
                        <div className="playlistAlbum">
                            <div><img src={item.img} /> </div>     
                                <div className="albumInfo">
                                    <h2>{item.name}</h2>
                                    <p>{item.length} minutes</p>
                                </div>
                            <div className="playlistButtonsDiv">
                                <div>
                                    <p># of times in playlist</p>
                                    <button disabled>-</button>
                                    <input className="numTimes" value="1"></input>
                                    <button>+</button>
                                </div>
                                <div><button>Remove</button></div>
                            </div>
                        </div>
                    )}
                    <p>Playlist Length: {this.state.length} minutes</p>
                </div>


            </div>        
        )
    }


}
