    constructor(props) {
        super(props);
        this.state = {
            tonyWinner: "All", 
            onBroadway: "All"
        };
    }

    onSelectFilterTonyWinners = event => {
        this.setState({
            tonyWinner: event
        })
    };
    onSelectFilterOnBroadway = event => {
        this.setState({
            onBroadway: event
        })
    };

    matchesFilterTonyWinner = item => {
        // all items should be shown when no filter is selected
        if(this.state.size === 'All') { 
            return true
        } else if (this.state.tonyWinner === item.tony) {
            return true
        } else {
            return false
        }
    }



