                <Navbar bg="light" variant="light">
                    <Navbar.Brand>Tony Winner for Best Musical:</Navbar.Brand>
                    <Nav className="mr-auto">
                        <Nav.Link eventKey="All" onSelect={this.onSelectFilterTonyWinner}>All</Nav.Link>
                        <Nav.Link eventKey="Yes" onSelect={this.onSelectTonyWinner}>Yes</Nav.Link>
