import React, { Component } from 'react';
import DisplayList from './DisplayList'

export default class FilteredList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            tonyWinner: "All", 
            onBroadway: "All"
        };
    }

    onSelectFilterSize = event => {
        this.setState({
            tonyWinner: event
        })
    };


    render(){
        return(
            <div>
                <DisplayList list={this.props.list} />
            </div>        
        )
    }


}
