import React, { Component } from 'react';
import DisplayList from './DisplayList'

export default class FilteredList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            tonyWinner: "All", 
            onBroadway: "All"
        };
    }

    onSelectFilterTonyWinners = event => {
        this.setState({
            tonyWinner: event
        })
    };
    onSelectFilterOnBroadway = event => {
        this.setState({
            onBroadway: event
        })
    };

    matchesFilterTonyWinner = item => {
        // all items should be shown when no filter is selected
        if(this.state.size === 'All') { 
            return true
        } else if (this.state.tonyWinner === item.tony) {
            return true
        } else {
            return false
        }
    }



    render(){
        return(
            <div>
               <div className="filterDiv">
                    <p>Currently Running on Broadway: <button>All</button> <button>Yes</button> <button>No</button></p>
                    <p>Won Tony Award for Best Musical: <button>All</button> <button>Yes</button> <button>No</button></p>
                    <p>Sort by Length: <button>Shortest to Longest</button> <button>Longest to Shortest</button></p>
                </div>
                <DisplayList list={this.props.list} />
            </div>        
        )
    }


}
