import React, { Component } from 'react';
import { Button} from 'react-bootstrap'
import './DisplayList.css';


export default class DisplayList extends React.Component {
    constructor(props) {
        super(props);
    }

    albumCards(){
        <div className="cardContainer">
        {this.props.list.map(item => 
            <div key={item.name} className="card">
                <img src={item.img} alt={item.name}/>
                <div className="cardInfo">
                    <h2>{item.name}</h2>
                    <p>Won Tony Award for Best Musical: {item.tony}</p>
                    <p>Currently Running on Broadway: {item.running}</p>
                    <p>Album Length: {item.length} minutes</p>
                    <div style={{textAlign: "center"}}><Button onClick={() => this.props.add(item)}>Add to Playlist</Button> </div>
                </div>
            </div>
        )}
    </div>
    }

    render(){
        return(
               this.props.inCart?console.log("HI") : this.albumCards()
        )
    }


}
